import './styles.css'
import { useState } from 'react';

function Square({ box }) {
  const [val, setVal] = useState(null);

  function handleClick(box) {
    setVal('X');
  }

  return <button className="square" id={box} onClick={() => handleClick({box})}>{val}</button>;
}

function BoardRow({row}) {
  return(
    <div className="board-row">
      <Square box={row} />
      <Square box={row + 1}/>
      <Square box={row + 2}/>
    </div>
  );
}

function Board() {
  const [Board, updateBoard] = useState(Array(9).fill(null));
