function Square({ box }) {
  const [val, setVal] = useState(null);

  function handleClick(box) {
    setVal('X');
  }

  return <button className="square" id={box} onClick={() => handleClick({box})}>{val}</button>;
}

function BoardRow({row}) {
  return(
    <div className="board-row">
      <Square box={row} />
      <Square box={row + 1}/>
      <Square box={row + 2}/>
    </div>
  );
}

function Board() {
  const row = 1;
  return (
    <div className='board'>
      <BoardRow row={row} />
      <BoardRow row={row + 3}/>
      <BoardRow row={row + 6}/>
    </div>
  );
}

export default Board;
