import { useState } from "react";

function Button(name) {
  const [count, setCount] = useState(0);

  function handleClick() {
    setCount(count + 1);
  }

  {
    (count === 0) ? (
      <button onClick={handleClick}>
      Clicked {count} times
    </button>
    ) : (
      <button onClick={handleClick}>
        Clicked {count} times
      </button>
    )
  }

  return(
    <button onClick={handleClick}>
      Clicked {count} times
    </button>
  );
}

function App() {
  return (
    <div>
      <Button name = "Button 1" />
      <Button name = "Button 2" />
    </div>
  );
}
export default App;
