import './styles.css'
import { useState } from 'react';

function Square({ box }) {
  return <button className="square" id={box} onClick={() => handleClick({box})}>{val}</button>;
}

function BoardRow({row}) {
  return(
    <div className="board-row">
      <Square box={row} />
      <Square box={row + 1}/>
      <Square box={row + 2}/>
    </div>
  );
}

function Board() {
  const [Board, updateBoard] = useState(Array(9).fill(null));
  function handleClick(square) {
    const newBoard = Board.slice();
    newBoard[square] = 'X';
    updateBoard(newBoard);
  }

  return (
    <div className='board'>
      <BoardRow row={row} />
      <BoardRow row={row + 3}/>
      <BoardRow row={row + 6}/>
    </div>
  );
}

export default Board;
