import './styles.css'
import { useState } from 'react';

function Square({ box }) {

  return <button className="square" id={box} onClick={() => handleClick({box})}>{val}</button>;
}

function BoardRow({row}) {
  return(
    <div className="board-row">
      <Square box={row} />
      <Square box={row + 1}/>
      <Square box={row + 2}/>
    </div>
  );
}

function Board() {
  const [board, updateBoard] = useState(Array(9).fill(null));
  function handleClick(square) {
    const newBoard = board.slice();
    newBoard[square] = 'X';
    updateBoard(newBoard);
  }

  return (
    <div className='board'>
      <div className="board-row">
        <Square value={board[0]} onSquareClick={() => handleClick(0)} />
        <Square value={board[1]} onSquareClick={() => handleClick(1)} />
        <Square value={board[2]} onSquareClick={() => handleClick(2)} />
      </div>
      <div className="board-row">
        <Square value={squares[3]} onSquareClick={() => handleClick(3)} />
        <Square value={squares[4]} onSquareClick={() => handleClick(4)} />
        <Square value={squares[5]} onSquareClick={() => handleClick(5)} />
      </div>
      <div className="board-row">
        <Square value={squares[6]} onSquareClick={() => handleClick(6)} />
        <Square value={squares[7]} onSquareClick={() => handleClick(7)} />
        <Square value={squares[8]} onSquareClick={() => handleClick(8)} />
      </div>
    </div>
  );
}

export default Board;
