import './styles.css'
import { useState } from 'react';

function Square({ box }) {
  return <button className="square" id={value}>{value}</button>;
}

function BoardRow({row}) {
  return(
    <div className="board-row">
      <Square box={row} />
      <Square col={row + 1}/>
      <Square col={row + 2}/>
    </div>
  );
}

function Board() {
  const row = 1;
  return (
    <div className='board'>
      <BoardRow row={row} />
      <BoardRow row={row + 3}/>
      <BoardRow row={row + 6}/>
    </div>
  );
}

export default Board;
